function milesightDeviceEncode(payload) {
    var encoded = [];
    if (payload.reboot != null) {
        encoded = encoded.concat(reboot(payload.reboot));
    }
    if (payload.report_interval != null) {
        encoded = encoded.concat(setReportInterval(payload.report_interval));
    }
    if (payload.?child_lock_config.size() > 0) {
        encoded = encoded.concat(setChildLock(payload.child_lock_config));
    }
    if (payload.timezone != null) {
        encoded = encoded.concat(setTimezone(payload.timezone));
    }
    if (payload.?dst_config.size() > 0) {
        encoded = encoded.concat(setDaylightSavingTime(payload.dst_config));
    }
    if (payload.?multicast_group_config.size() > 0) {
        encoded = encoded.concat(setMulticastGroupConfig(payload.multicast_group_config));
    }
    return encoded;
}

function reboot(reboot) {
    validateEnableValue("Reboot", reboot);
    return reboot === 0 ? [] : [0xff, 0x10, 0xff];
}
function setReportInterval(report_interval) {
    isNumber("Reporting Interval", report_interval);
    return [0xFF, 0x8E, 0x00, report_interval, report_interval >>> 8];
}
function setChildLock(child_lock_config) {
    var button_mask_bit_offset = { power_button: 0, up_button: 1, down_button: 2, fan_button: 3, mode_button: 4, reset_button: 5 };
    var arrayChildLock = new int[6];
    foreach(button : child_lock_config.entrySet()){
        validateEnableValue("Child_lock_config." + button.key, button.value);
        var ind = button_mask_bit_offset[button.key];
        arrayChildLock[5 - ind] = button.value;
    }
    var intChildLock = parseBinaryArrayToInt(arrayChildLock);
    return [0xFF, 0x25, 0xFF, intChildLock];
}
function setTimezone(timezone) {
    isNumber("UTC Time Zone", timezone);
    var tz = timezone * 60;
    return [0xFF, 0xBD, tz & 0xFF, (tz >>> 8) & 0xFF];
}
function setDaylightSavingTime(dstConfig) {
    validateEnableValue("Dst_config.enable", dstConfig.enable);
    isNumber("Dst_config.bias", dstConfig.bias);
    if (dstConfig.?start_time.size() > 0 && dstConfig.?end_time.size() > 0) {
        var rezDstConfig = [0xFF, 0xBA, 0, 0];
        if (dstConfig.enable === 0) {
            return rezDstConfig.concat([0,0,0,0,0,0,0,0]);
        }
        rezDstConfig[2] = dstConfig.enable;
        rezDstConfig[3] = dstConfig.bias;
        var startDate = validateDstConfigDateTime(dstConfig.start_time.month, dstConfig.start_time.week, dstConfig.start_time.weekday, dstConfig.start_time.time, "Start") ;
        var endDate = validateDstConfigDateTime(dstConfig.end_time.month, dstConfig.end_time.week, dstConfig.end_time.weekday, dstConfig.end_time.time, "End") ;
        rezDstConfig = rezDstConfig.concat(startDate);
        return rezDstConfig.concat(endDate);
    }
}
function setMulticastGroupConfig(multicast_group_config) {
    var groupName;
    var groupValue;
    var bitArray = new int [8];
    for (var i = 1; i < 5; i++) {
        groupName = "group"+ i + "_enable";
        groupValue = multicast_group_config[groupName];
        if (groupValue != null) {
            validateEnableValue(groupName, groupValue);
            bitArray[4-i] = 1;
            bitArray[8-i] = groupValue;
        }
    }
    return [0xFF, 0x82, parseBinaryArrayToInt(bitArray)];
}

function validateDstConfigDateTime(month, week, weekday, dstTime, startEnd) {
    var timeSplit = dstTime.split(":");
    var times = [parseInt(timeSplit[0]), parseInt(timeSplit[1])];
    if (times[0] < 0 || times[0] > 24) {
        raiseError(startEnd + " hours [" + times[0] +"]. Must be between 0 and 24.");
    }
    if (times[1] < 0 || times[1] > 60) {
        raiseError(startEnd + " minutes [" + times[1] +"]. Must be between 0 and 60.");
    }
    if (month < 1 || month > 12) {
        raiseError(startEnd + " month [" + month +"]. Must be between 1 and 12.");
    }
    if (week < 1 || week > 5) {
        raiseError(startEnd + " week [" + week +"]. Must be between 1 and 5.");
    }
    if (weekday < 1 || weekday > 7) {
        raiseError(startEnd + " weekday [" + weekday +"]. Must be between 1 and 7.");
    }
    var dayWeek = (week << 4) | weekday;
    var time =  times[0] *60 + times[1];
    return [month, dayWeek, time & 0xFF, (time >>> 8) & 0xFF];
}
function validateEnableValue(nameValue, value) {
    var enable_values = [0, 1];
    if (enable_values.indexOf(value) === -1) {
        raiseError(nameValue + " [" + value + "]. Must be one of [0, 1]");
    }
}
function isNumber(nameValue, value) {
    if (value != null && isDecimal(value) === -1) {
        raiseError(nameValue + " must be a number.");
    }
}


// Result object with encoded downlink payload
var result = {

    // downlink data content type: JSON, TEXT or BINARY (base64 format)
    contentType: "JSON",

    fPort: 85,
    // downlink data
    data: JSON.stringify(data),
    dataHex: base64ToHex(data.bytes),

    // Optional metadata object presented in key/value format
    metadata: {
        topic: metadata['deviceType']+'/'+metadata['deviceName']+'/'+metadata['ss_serialNumber']+'/upload'
    }

};

return result;
